    }

    if (selectsRange && swapRange && startDate && selectingDate) {
      return isSameDay(
        day,
        isBefore(selectingDate, startDate) ? selectingDate : startDate,
      );
    }

    return isSameDay(day, startDate);
  };

  isSelectingRangeEnd = () => {
