  render(): React.ReactElement {
    const {
      weekNumber,
      isWeekDisabled,
      ariaLabelPrefix = WeekNumber.defaultProps.ariaLabelPrefix,
      onClick,
    } = this.props;

    const weekNumberClasses = {
      "react-datepicker__week-number": true,
      "react-datepicker__week-number--clickable": !!onClick && !isWeekDisabled,
