  dropdownRef: React.RefObject<HTMLDivElement | null>;
  yearOptionButtonsRef: Record<number, HTMLDivElement | null> = {};

  handleOptionKeyDown = (year: number, e: React.KeyboardEvent): void => {
    switch (e.key) {
