import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "../styles/DateBox.css";

const DateBox = ({ startDate, endDate, setStartDate, setEndDate }) => {
  return (
    <div className="date-box">
      {/* 转专 转 */}
      <div className="date-field">
        <label>From:</label>
        <DatePicker
          selected={startDate}
          onChange={(date) => setStartDate(date)}
          selectsStart
          startDate={startDate}
          endDate={endDate}
          placeholderText="Select start date"
          dateFormat="yyyy-MM-dd"
          popperPlacement="top-start"                // 猬锔 驻转 注
          popperClassName="datebox-popper-top"      //  注爪 转
          shouldCloseOnSelect
          popperModifiers={[
            {
              name: "offset",
              options: {
                offset: [0, 10], // 专 拽  砖 
              },
            },
            {
              name: "preventOverflow",
              options: {
                rootBoundary: "viewport",
                tether: false, // 注 爪转 转专
              },
            },
          ]}
        />
      </div>

      {/* 转专 住 */}
      <div className="date-field">
        <label>Until:</label>
        <DatePicker
          selected={endDate}
          onChange={(date) => setEndDate(date)}
          selectsEnd
          startDate={startDate}
          endDate={endDate}
          minDate={startDate}
          placeholderText="Select end date"
          dateFormat="yyyy-MM-dd"
          popperPlacement="top-start"
          popperClassName="datebox-popper-top"
          shouldCloseOnSelect
          popperModifiers={[
            {
              name: "offset",
              options: {
                offset: [0, 10],
              },
            },
            {
              name: "preventOverflow",
              options: {
                rootBoundary: "viewport",
                tether: false,
              },
            },
          ]}
        />
      </div>
    </div>
  );
};

export default DateBox;
