import React from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import '../styles/DateBox.css';

const DateBox = ({ startDate, endDate, setStartDate, setEndDate, onApply }) => {
  
  // פונקציה לניקוי התאריכים
  const clearDates = () => {
    setStartDate(null);
    setEndDate(null);
  };

  // בדיקה אם יש תאריכים פעילים
  const hasActiveDates = startDate || endDate;

  return (
    <div className="date-box">
      <div className="date-input-group">
        <label>From: </label>
        <DatePicker
          selected={startDate}
          onChange={(date) => {
            setStartDate(date);
            // אם בחרו תאריך התחלה אחרי תאריך הסיום, נמחק את תאריך הסיום
            if (endDate && date && date > endDate) {
              setEndDate(null);
            }
          }}
          selectsStart
          startDate={startDate}
          endDate={endDate}
          placeholderText="Select start date"
          dateFormat="dd/MM/yyyy"
          isClearable
          showMonthDropdown
          showYearDropdown
          dropdownMode="select"
        />
      </div>

      <div className="date-input-group">
        <label>Until: </label>
        <DatePicker
          selected={endDate}
          onChange={(date) => setEndDate(date)}
          selectsEnd
          startDate={startDate}
          endDate={endDate}
          minDate={startDate}
          placeholderText="Select end date"
          dateFormat="dd/MM/yyyy"
          isClearable
          showMonthDropdown
          showYearDropdown
          dropdownMode="select"
        />
      </div>

      <div className="date-actions">
        {onApply && (
          <button 
            className="apply-dates" 
            onClick={onApply}
            disabled={!hasActiveDates}
          >
            Apply Dates
          </button>
        )}
        
        {hasActiveDates && (
          <button 
            className="clear-dates" 
            onClick={clearDates}
            type="button"
          >
            Clear Dates
          </button>
        )}
      </div>
    </div>
  );
};

export default DateBox;
