import '../styles/ShowCard.css';
import { useNavigate } from "react-router-dom"; // üëà ◊î◊ï◊°◊ô◊§◊ô ◊ê◊™ ◊ñ◊î

function ShowCard({ showId,hallName,bandName, showDate, ticketPrice, picture, city, country, street }) {

  const navigate = useNavigate(); // üëà ◊ô◊ï◊¶◊® ◊§◊ï◊†◊ß◊¶◊ô◊ô◊™ ◊†◊ô◊ï◊ï◊ò

  return (
    <div className="show-card">
      <img src={picture} alt={bandName} className="show-image" />
      <div className="show-details">
        <p className="show-band">{bandName}</p>
        <p className="show-date"><label>Date: </label>{showDate}</p>
        <label>Hall: </label>{hallName || "Unknown Hall"}
        <p className="show-location">
          <label>Location: </label>
          {city && country && street ? `${country}, ${city}, ${street}` : "Unknown"}
        </p>
        <p className="show-price"><label>Price: </label>{ticketPrice} ‚Ç™</p>
        <button
          className="buy-ticket"
          onClick={() => navigate(`/shows/${showId}/buy`)} // üëà ◊¢◊õ◊©◊ô◊ï ◊ñ◊î ◊ô◊¢◊ë◊ï◊ì
        >
          Buy Ticket
        </button>
      </div>
    </div>
  );
}

export default ShowCard;
